-----------------------VIEW 1 CUSTOMER INFORMATION-----------------------

CREATE OR REPLACE VIEW CUSTOMER_INFORMATION AS
SELECT (CUS_F_NAME|| ' ' || CUS_L_NAME) FULL_NAME, (CUS_CONTACT_NO||','||CUS_ALTERNATE_NO||','||CUS_EMAIL_ID) CUSTOMER_CONTACT_INFO, (CUS_ADDRESS_1|| ',' || CUS_ADDRESS_2|| ',' ||CUS_CITY|| ',' ||CUS_STATE|| ',' ||CUS_COUNTRY|| ',' ||CUS_ZIP) CUSTOMER_ADDRESS
FROM CUSTOMER;

-----------------VIEW 2 CUSTOMER_TRANSACTION_HISTORY-----------------------

CREATE OR REPLACE VIEW CUSTOMER_TRANSACTION_HISTORY AS
SELECT CT.ORDER_NUMBER, MAX (CT.ORDER_CUS_ID) CUSTOMER_ID,MAX(CT.ORDER_DATE) ORDER_DATE, COUNT(PS.SALE_STOCK_ID) NUMBER_OF_PRODUCTS,SUM(PS.ORDER_QTY) TOTAL_QUANTITY, ROUND(SUM((ST.STOCK_PRICE/ST.STOCK_QUANTITY)*PS.ORDER_QTY),1) ORDER_PRICE
FROM CUSTOMER_TRANSACTION CT JOIN PRODUCT_SALE PS ON CT.ORDER_NUMBER = PS.ORDER_NUMBER
JOIN STOCK ST ON PS.SALE_STOCK_ID=ST.STOCK_ID
GROUP BY CT.ORDER_NUMBER;

-----------------------VIEW 3 PRODUCT_SALE_VIEW-----------------------

CREATE OR REPLACE VIEW PRODUCT_SALE_VIEW AS
SELECT PS.SALE_ID,PS.ORDER_NUMBER,PS.SALE_STOCK_ID,PS.ORDER_QTY, ST.STOCK_NAME, ROUND(((ST.STOCK_PRICE/ST.STOCK_QUANTITY)*PS.ORDER_QTY),2) PRODUCT_PRICE,ST.MANUFACTURER_NAME 
FROM PRODUCT_SALE PS JOIN STOCK ST ON PS.SALE_STOCK_ID = ST.STOCK_ID;

---------------------VIEW 4 RETURN TRANSACTION VIEW-------------------------------

CREATE OR REPLACE VIEW RETURN_TRANSACTION_VIEW AS
SELECT RT.RETURN_CUSTOMER_ID, (C.CUS_F_NAME ||' '||C.CUS_L_NAME)AS CUSTOMER_NAME, C.CUS_CONTACT_NO, C.CUS_EMAIL_ID,RPS.RETURN_STOCK_ID
FROM return_transaction RT JOIN CUSTOMER C ON RT.RETURN_CUSTOMER_ID = C.CUSTOMER_ID 
JOIN return_product_sale RPS ON RT.RETURN_NUMBER = RPS.RETURN_NUMBER;


------------------------VIEW 5 RETURN SALE VIEW--------------------------------

CREATE OR REPLACE VIEW RETURN_SALE_VIEW AS
select rt.return_customer_id cus_id, c.cus_f_name ||' '|| c.cus_l_name customer_name, rt.return_number, rps.return_stock_id stock_id_returned, s.stock_name ,rps.return_qty
from return_transaction rt join return_product_sale rps on rt.return_number = rps.return_number
                           join stock s on rps.return_stock_id = s.stock_id
                           join customer c on rt.return_customer_id = c.customer_id;

------------------------VIEW 6 ADMIN VIEW----------------------------------

CREATE OR REPLACE VIEW ADMIN_VIEW AS
SELECT ADMIN_ID,(ADMIN_F_NAME||' '||ADMIN_L_NAME) NAME,ADMIN_EMAIL_ID,ADMIN_CONTACT_NO
FROM ADMIN;

----------------------VIEW 7 STOCK VIEW------------------------

CREATE OR REPLACE VIEW STOCK_VIEW AS
SELECT STOCK_NAME, SPECS, MANUFACTURER_NAME, stock_quantity
FROM STOCK;

--------------------------VIEW 8 SUPPLIER VIEW--------------------------

CREATE OR REPLACE VIEW SUPPLIER_VIEW AS
SELECT SUPPLIER_NAME,(SUPPLIER_ADDRESS_1|| ',' || SUPPLIER_ADDRESS_2|| ',' ||SUPPLIER_CITY|| ',' ||SUPPLIER_STATE||  ',' ||SUPPLIER_ZIP) SUPPLIER_ADDRESS,(SUPPLIER_CONTACT_NO||','||SUPPLIER_EMAIL_ID) SUPPLIER_CONTACT_INFO
FROM SUPPLIERS;

-------------------------VIEW 9 DAILY SALES REPORT----------------------------

CREATE OR REPLACE VIEW DAILY_SALES_REPORT AS
SELECT EXTRACT(DAY FROM CT.ORDER_DATE) AS DAY, EXTRACT(MONTH FROM CT.ORDER_DATE) MONTH, SUM(ORDER_QTY) QUANTITY,  (ROUND(SUM((ST.STOCK_PRICE/ST.STOCK_QUANTITY)*PS.ORDER_QTY),1)) ORDER_PRICE
FROM CUSTOMER_TRANSACTION CT JOIN PRODUCT_SALE PS ON CT.ORDER_NUMBER = PS.ORDER_NUMBER
JOIN STOCK ST ON PS.SALE_STOCK_ID = ST.STOCK_ID
GROUP BY EXTRACT(DAY FROM CT.ORDER_DATE) , EXTRACT(MONTH FROM CT.ORDER_DATE);


----------------------VIEW 10 MONTHLY SALES REPORT------------------------

CREATE OR REPLACE VIEW MONTHLY_SALES_REPORT AS
SELECT EXTRACT(MONTH FROM CT.ORDER_DATE) AS MONTH, SUM(ORDER_QTY) QUANTITY,  (ROUND(SUM((ST.STOCK_PRICE/ST.STOCK_QUANTITY)*PS.ORDER_QTY),1)) ORDER_PRICE
FROM CUSTOMER_TRANSACTION CT JOIN PRODUCT_SALE PS ON CT.ORDER_NUMBER = PS.ORDER_NUMBER
JOIN STOCK ST ON PS.SALE_STOCK_ID = ST.STOCK_ID
GROUP BY EXTRACT(MONTH FROM CT.ORDER_DATE);


-----------------------VIEW 11 TOP_SELLING_PRODUCT_REPORT-----------------------------------

CREATE OR REPLACE VIEW TOP_SELLING_PRODUCT_REPORT AS
SELECT PS.SALE_STOCK_ID , MAX(ST.STOCK_NAME) PRODUCT_NAME,MAX(PS.ORDER_QTY) QUANTITY, (ROUND(SUM((ST.STOCK_PRICE/ST.STOCK_QUANTITY)*PS.ORDER_QTY),1)) ORDER_PRICE
FROM PRODUCT_SALE PS JOIN STOCK ST ON PS.SALE_STOCK_ID=ST.STOCK_ID 
GROUP BY PS.SALE_STOCK_ID
ORDER BY 4 DESC, 3 DESC;

